package com.project.util;

import org.apache.commons.lang3.RandomStringUtils;

import javax.mail.Message;
import javax.mail.Session;
import javax.mail.Transport;
import javax.mail.internet.InternetAddress;
import javax.mail.internet.MimeMessage;
import java.util.Properties;

public class SMTPUtil {
    static final String FROM = "nafreedom5@naver.com";
    static final String FROMNAME = "Mina";
    static final String SMTP_USERNAME = "nafreedom5@naver.com";
    static final String SMTP_PASSWORD = "WILLRAIN12!@";

    static final String HOST = "smtp.naver.com";
    static final int PORT = 587;

    static final String SUBJECT = "말랑말랑 Form 참여를 위한 설문 인증코드를 알려드립니다.";

    private static String getBody(String surveyTitle, String password, String surveyLink){
        String BODY = String.join(
                System.getProperty("line.separator"),
                "<div style='width: 600px;background: aliceblue;'>",
                "<div style='padding: 5px;'>",
                "<div style='text-align: center; background-color: #c4eaf7;padding: 10px;'>",
                "<img src='data:image/png;base64,' width='200'>",
                "</div>",
                "<div style='padding: 50px 20px;font-size: 20px;line-height: 40px;'>",
                "<p style='margin: 0;font-size: 25px;'>안녕하세요.</p>",
                "말랑말랑 Form 참여를 위한 인증코드를 알려드립니다.",
                "<br>'{{survey_title}}' 에 참여를 원하신다면",
                "<br>설문 응답 페이지에 접속하여 인증코드를 입력바랍니다.",
                "<p style='font-size: 22px;padding-top:20px'>설문 인증 코드  :  {{password}}</p>",
                "</div>",
                "<div style='height: 100px;font-size: 20px;text-align: center;background-color: lightblue;'>",
                "<a style='text-decoration: underline;line-height: 100px;cursor:pointer;' href='{{survey_link}}'>설문 응답하기</a>",
                "</div>",
                "</div>",
                "</div>"
        );

        BODY = BODY.replace("{{survey_title}}",surveyTitle);
        BODY = BODY.replace("{{password}}",password);
        BODY = BODY.replace("{{survey_link}}",surveyLink);

        return BODY;
    }

    public static String sendCertEmail(String emailAddr, String surveyTitle, String surveyLink) throws Exception{
        String returnPassword = null;

        Properties props = System.getProperties();
        props.put("mail.transport.protocol", "smtp");
        props.put("mail.smtp.port", PORT);
        props.put("mail.smtp.starttls.enable", "true");
        props.put("mail.smtp.auth", "true");
        Session session = Session.getDefaultInstance(props);

        MimeMessage msg = new MimeMessage(session);
        msg.setFrom(new InternetAddress(FROM, FROMNAME));
        msg.setRecipient(Message.RecipientType.TO, new InternetAddress(emailAddr));
        msg.setSubject(SUBJECT);

        int length = 10;
        boolean useLetters = true;
        boolean useNumbers = false;
        returnPassword = RandomStringUtils.random(length, useLetters, useNumbers);

        System.out.println(returnPassword);
        msg.setContent(getBody(surveyTitle,returnPassword,surveyLink), "text/html;charset=euc-kr");

        Transport transport = session.getTransport();
        try {
            transport.connect(HOST, SMTP_USERNAME, SMTP_PASSWORD);
            transport.sendMessage(msg, msg.getAllRecipients());
        } catch (Exception ex) {
            ex.printStackTrace();
        } finally {
            transport.close();
        }

        return returnPassword;
    }
}
